import React, { useState } from 'react';
import { TextField, Button, Paper, Typography, Grid } from '@mui/material';
import axios from 'axios';

function LoginRegister({ setUser }) {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const url = isLogin ? 'http://localhost:5000/login' : 'http://localhost:5000/register';
      const data = isLogin ? { username: formData.username, password: formData.password } : formData;

      const res = await axios.post(url, data, { withCredentials: true });

      if (isLogin && res.data.user) {
        localStorage.setItem('user', JSON.stringify(res.data.user));
        setUser(res.data.user);
      }

      alert(res.data.message);
    } catch (err) {
      alert(err.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <Paper elevation={3} style={{ padding: 32, maxWidth: 400, margin: 'auto', marginTop: 100 }}>
      <Typography variant="h5" align="center" gutterBottom>
        {isLogin ? 'Login' : 'Register'}
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2} direction="column">
          <Grid item>
            <TextField name="username" label="Username" fullWidth onChange={handleChange} required />
          </Grid>
          {!isLogin && (
            <Grid item>
              <TextField name="email" label="Email" fullWidth onChange={handleChange} required />
            </Grid>
          )}
          <Grid item>
            <TextField name="password" label="Password" type="password" fullWidth onChange={handleChange} required />
          </Grid>
          <Grid item>
            <Button type="submit" variant="contained" fullWidth>
              {isLogin ? 'Login' : 'Register'}
            </Button>
          </Grid>
        </Grid>
      </form>
      <Typography align="center" style={{ marginTop: 16 }}>
        {isLogin ? "Don't have an account?" : 'Already have an account?'}{' '}
        <Button onClick={() => setIsLogin(!isLogin)}>{isLogin ? 'Register' : 'Login'}</Button>
      </Typography>
    </Paper>
  );
}

export default LoginRegister;
