import React from "react";
import { FaUser,FaEnvelope } from "react-icons/fa";
import { FaLock } from "react-icons/fa";
import './LoginRegister.css';
const LoginRegister = ()=>{

    const [action, setAction]=useState('');
    const regsiterLink= ()=>{
        setAction('active');
    };
    const loginLink= ()=>{
        setAction('');
    };
    return(
        <div className={`wrapper ${action}`}>
        <div className="form-box login">
            <form action=" ">
                <h1>Login</h1>
                <div className="input-box">
                    <input type="text" placeholder="Username" requierd/>
                    <FaUser className="icon" />
                </div>
                <div className="input-box">
                    <input type="password" placeholder="Password" requierd/>
                    <FaLock className="icon"/>
                </div>
                <div className="remember-forgot">
                 <label><input type="checkbox"/>Remember me</label>
                 <a href="#">Forgot password?</a>
                </div>
                <button type="submit">Login</button>
                <div className="register-link">
                <p> Don't have an account? <a href="#" onClick={regsiterLink}>Register</a></p>
                </div>

            </form>
        </div>
        <div className="form-box register">
            <form action=" ">
                <h1>Registration</h1>
                <div className="input-box">
                    <input type="text" placeholder="Username" requierd/>
                    <FaUser className="icon" />
                </div>
                <div className="input-box">
                    <input type="text" placeholder="Email" requierd/>
                    <FaEnvelope className="icon" />
                </div>
                <div className="input-box">
                    <input type="password" placeholder="Password" requierd/>
                    <FaLock className="icon"/>
                </div>
                <div className="remember-forgot">
                 <label><input type="checkbox"/>I agree to the terms & conditions</label>
                 <a href="#">Forgot password?</a>
                </div>
                <button type="submit">Register</button>
                <div className="register-link">
                <p> Already have an account? <a href="#"onClick={loginLink}>Login</a></p>
                </div>

            </form>
        </div>




        </div>
    );
};
export default LoginRegister;
