import React, { useEffect } from 'react';
import './Home.css';

export default function Home() {
  useEffect(() => {
    const menuIcon = document.querySelector('.menu-icon');
    const sidebar = document.querySelector('.sidebar');
    const contentArea = document.querySelector('.content-area');
    const menuItems = document.querySelectorAll('.menu-item');
    const contentSections = document.querySelectorAll('.content-section');

    menuIcon.addEventListener('click', () => {
      sidebar.classList.toggle('active');
      contentArea.classList.toggle('sidebar-active');
    });

    menuItems.forEach(item => {
      item.addEventListener('click', function () {
        menuItems.forEach(i => i.classList.remove('active'));
        this.classList.add('active');

        contentSections.forEach(section => {
          section.style.display = 'none';
        });

        const sectionId = this.getAttribute('data-section') + '-section';
        const selectedSection = document.getElementById(sectionId);
        if (selectedSection) {
          selectedSection.style.display = 'block';
        }
      });
    });

    document.getElementById('profile-section').style.display = 'block';
  }, []);

  return (
    <div className="dashboard">
      <div className="menu-icon">â‰¡</div>

      <div className="sidebar">
        <div className="user-profile">
          <div className="avatar">PB</div>
          <div className="user-info">
            <h3>P. Balaji</h3>
            <p>Traveler</p>
          </div>
        </div>
        <div className="menu-items">
          <div className="menu-item active" data-section="profile">Profile</div>
          <div className="menu-item" data-section="flights">Flights</div>
          <div className="menu-item" data-section="bookings">Bookings</div>
          <div className="menu-item" data-section="support">Support</div>
        </div>
      </div>

      <div className="content-area">
        <div id="profile-section" className="content-section">
          <h2>Welcome to Swadeshi Airlines</h2>
          <p>This is your profile overview.</p>
        </div>

        <div id="flights-section" className="content-section" style={{ display: 'none' }}>
          <h2>Upcoming Flights</h2>
          <p>No upcoming flights available right now.</p>
        </div>

        <div id="bookings-section" className="content-section" style={{ display: 'none' }}>
          <h2>Bookings</h2>
          <p>You havenâ€™t made any bookings yet.</p>
        </div>

        <div id="support-section" className="content-section" style={{ display: 'none' }}>
          <h2>Help & Support</h2>
          <p>Reach us at help@swadeshiairlines.com</p>
        </div>
      </div>
    </div>
  );
}
