import React, { useEffect } from 'react';
import './Home.css';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();

  useEffect(() => {
    const menuIcon = document.querySelector('.menu-icon');
    const sidebar = document.querySelector('.sidebar');
    const contentArea = document.querySelector('.content-area');

    const toggleSidebar = () => {
      sidebar.classList.toggle('active');
      contentArea.classList.toggle('sidebar-active');
    };

    menuIcon.addEventListener('click', toggleSidebar);

    const menuItems = document.querySelectorAll('.menu-item');
    const contentSections = document.querySelectorAll('.content-section');

    menuItems.forEach(item => {
      item.addEventListener('click', function () {
        menuItems.forEach(i => i.classList.remove('active'));
        this.classList.add('active');
        contentSections.forEach(section => {
          section.style.display = 'none';
        });
        const sectionId = this.getAttribute('data-section') + '-section';
        const selectedSection = document.getElementById(sectionId);
        if (selectedSection) {
          selectedSection.style.display = 'block';
        }
      });
    });

    document.getElementById('profile-section').style.display = 'block';

    const upcomingFlights = [
      {
        flightNumber: 'AA123',
        from: 'New York',
        to: 'London',
        date: '2024-03-15',
        time: '10:00 AM',
      },
      {
        flightNumber: 'BA456',
        from: 'London',
        to: 'Paris',
        date: '2024-03-20',
        time: '02:30 PM',
      },
    ];

    const recentBookings = [
      {
        bookingId: 'BK12345',
        flightNumber: 'AA123',
        date: '2024-03-01',
        status: 'Confirmed',
      },
      {
        bookingId: 'BK12346',
        flightNumber: 'BA456',
        date: '2024-02-28',
        status: 'Completed',
      },
    ];

    const updateCards = () => {
      const upcomingFlightsCard = document.querySelector('.card:nth-child(1) p');
      const recentBookingsCard = document.querySelector('.card:nth-child(2) p');

      if (upcomingFlightsCard && recentBookingsCard) {
        const flightsList = upcomingFlights
          .map(f => `${f.flightNumber}: ${f.from} â†’ ${f.to} (${f.date} ${f.time})`)
          .join('<br>');
        upcomingFlightsCard.innerHTML = flightsList;

        const bookingsList = recentBookings
          .map(b => `${b.bookingId}: ${b.flightNumber} (${b.date}) - ${b.status}`)
          .join('<br>');
        recentBookingsCard.innerHTML = bookingsList;
      }
    };

    updateCards();

    const searchForm = document.querySelector('.search-form');
    if (searchForm) {
      searchForm.addEventListener('submit', e => {
        e.preventDefault();
        const fromCity = document.getElementById('from-city').value;
        const toCity = document.getElementById('to-city').value;
        const departureDate = document.getElementById('departure-date').value;
        const returnDate = document.getElementById('return-date').value;
        const passengers = document.getElementById('passengers').value;
        const flightClass = document.getElementById('class').value;

        console.log('Search parameters:', {
          fromCity,
          toCity,
          departureDate,
          returnDate,
          passengers,
          flightClass,
        });

        // Optional: Call API here
      });
    }

    // Cleanup
    return () => {
      menuIcon.removeEventListener('click', toggleSidebar);
    };
  }, []);

  return (
    <div className="home-container">
      {/* Sidebar, header, content-area etc. should go here */}
      {/* For now this is just the shell */}
      <div className="sidebar">
        <div className="menu-icon">â˜°</div>
        <ul>
          <li className="menu-item active" data-section="profile">Profile</li>
          <li className="menu-item" data-section="flights">Flights</li>
          <li className="menu-item" data-section="bookings">Bookings</li>
        </ul>
      </div>

      <div className="content-area">
        <div id="profile-section" className="content-section">Profile Info</div>
        <div id="flights-section" className="content-section" style={{ display: 'none' }}>Upcoming Flights</div>
        <div id="bookings-section" className="content-section" style={{ display: 'none' }}>Recent Bookings</div>
      </div>
    </div>
  );
}
